-- Generated by AutomateDV (formerly known as dbtvault)

    WITH source_data AS (
    SELECT a.HUB_AIRCRAFTS_KEY, a.HASHDIFF, a.model, a.range, a.LOAD_DTS, a.RECORD_SOURCE
    FROM "postgres"."dwh_detailed"."stg_aircrafts" AS a
    WHERE a.HUB_AIRCRAFTS_KEY IS NOT NULL
),

latest_records AS (
    SELECT a.HUB_AIRCRAFTS_KEY, a.HASHDIFF, a.LOAD_DTS
    FROM (
        SELECT current_records.HUB_AIRCRAFTS_KEY, current_records.HASHDIFF, current_records.LOAD_DTS,
            RANK() OVER (
                PARTITION BY current_records.HUB_AIRCRAFTS_KEY
                ORDER BY current_records.LOAD_DTS DESC
            ) AS rank
        FROM "postgres"."dwh_detailed"."sat_aircrafts_detail" AS current_records
            JOIN (
                SELECT DISTINCT source_data.HUB_AIRCRAFTS_KEY
                FROM source_data
            ) AS source_records
                ON current_records.HUB_AIRCRAFTS_KEY = source_records.HUB_AIRCRAFTS_KEY
    ) AS a
    WHERE a.rank = 1
),

records_to_insert AS (
    SELECT DISTINCT stage.HUB_AIRCRAFTS_KEY, stage.HASHDIFF, stage.model, stage.range, stage.LOAD_DTS, stage.RECORD_SOURCE
    FROM source_data AS stage
    LEFT JOIN latest_records
    ON latest_records.HUB_AIRCRAFTS_KEY = stage.HUB_AIRCRAFTS_KEY
        AND latest_records.HASHDIFF = stage.HASHDIFF
    WHERE latest_records.HASHDIFF IS NULL

)

SELECT * FROM records_to_insert