    FROM "postgres"."dwh_detailed"."stg_airports" AS a
    WHERE a.HUB_AIRPORTS_KEY IS NOT NULL
),

latest_records AS (
    SELECT a.HUB_AIRPORTS_KEY, a.HASHDIFF, a.LOAD_DTS
    FROM (
        SELECT current_records.HUB_AIRPORTS_KEY, current_records.HASHDIFF, current_records.LOAD_DTS,
            RANK() OVER (
                PARTITION BY current_records.HUB_AIRPORTS_KEY
                ORDER BY current_records.LOAD_DTS DESC
            ) AS rank
        FROM "postgres"."dwh_detailed"."sat_airports_detail" AS current_records
