                SELECT DISTINCT source_data.HUB_AIRPORTS_KEY
                FROM source_data
            ) AS source_records
                ON current_records.HUB_AIRPORTS_KEY = source_records.HUB_AIRPORTS_KEY
    ) AS a
    WHERE a.rank = 1
),

records_to_insert AS (
    SELECT DISTINCT stage.HUB_AIRPORTS_KEY, stage.HASHDIFF, stage.airport_name, stage.city, stage.coordinates_lon, stage.coordinates_lat, stage.timezone, stage.LOAD_DTS, stage.RECORD_SOURCE
    FROM source_data AS stage
    LEFT JOIN latest_records
    ON latest_records.HUB_AIRPORTS_KEY = stage.HUB_AIRPORTS_KEY
        AND latest_records.HASHDIFF = stage.HASHDIFF
