    WITH source_data AS (
    SELECT a.HUB_FLIGHTS_KEY, a.HASHDIFF, a.flight_no, a.scheduled_departure, a.scheduled_arrival, a.status, a.actual_departure, a.actual_arrival, a.LOAD_DTS, a.RECORD_SOURCE
    FROM "postgres"."dwh_detailed"."stg_flights" AS a
    WHERE a.HUB_FLIGHTS_KEY IS NOT NULL
),

latest_records AS (
    SELECT a.HUB_FLIGHTS_KEY, a.HASHDIFF, a.LOAD_DTS
    FROM (
        SELECT current_records.HUB_FLIGHTS_KEY, current_records.HASHDIFF, current_records.LOAD_DTS,
            RANK() OVER (
                PARTITION BY current_records.HUB_FLIGHTS_KEY
                ORDER BY current_records.LOAD_DTS DESC
            ) AS rank
        FROM "postgres"."dwh_detailed"."sat_flights_detail" AS current_records
            JOIN (
                SELECT DISTINCT source_data.HUB_FLIGHTS_KEY
                FROM source_data
            ) AS source_records
                ON current_records.HUB_FLIGHTS_KEY = source_records.HUB_FLIGHTS_KEY
    ) AS a
    WHERE a.rank = 1
),

records_to_insert AS (
    SELECT DISTINCT stage.HUB_FLIGHTS_KEY, stage.HASHDIFF, stage.flight_no, stage.scheduled_departure, stage.scheduled_arrival, stage.status, stage.actual_departure, stage.actual_arrival, stage.LOAD_DTS, stage.RECORD_SOURCE
    FROM source_data AS stage
    LEFT JOIN latest_records
    ON latest_records.HUB_FLIGHTS_KEY = stage.HUB_FLIGHTS_KEY
        AND latest_records.HASHDIFF = stage.HASHDIFF
    WHERE latest_records.HASHDIFF IS NULL

)

