








WITH staging AS (
-- Generated by AutomateDV (formerly known as dbtvault)

    

WITH source_data AS (

    SELECT

    aircraft_code,
    model,
    range

    FROM "postgres"."dwh_detailed"."aircrafts"
),

derived_columns AS (

    SELECT

    aircraft_code,
    model,
    range,
    'POSTGRES_MASTER'::TEXT AS RECORD_SOURCE

    FROM source_data
),

hashed_columns AS (

    SELECT

    aircraft_code,
    model,
    range,
    RECORD_SOURCE,

    DECODE(MD5(NULLIF(UPPER(TRIM(CAST(aircraft_code AS VARCHAR))), '')), 'hex') AS HUB_AIRCRAFTS_KEY,

    DECODE(MD5(CONCAT(
        COALESCE(NULLIF(UPPER(TRIM(CAST(model AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(range AS VARCHAR))), ''), '^^')
    )), 'hex') AS HASHDIFF

    FROM derived_columns
),

columns_to_select AS (

    SELECT

    aircraft_code,
    model,
    range,
    RECORD_SOURCE,
    HUB_AIRCRAFTS_KEY,
    HASHDIFF

    FROM hashed_columns
)

SELECT * FROM columns_to_select
)

SELECT *, 
       ('1992-01-01')::TIMESTAMP AS LOAD_DTS,
       ('1992-01-01')::TIMESTAMP AS EFFECTIVE_FROM
FROM staging