-- Generated by AutomateDV (formerly known as dbtvault)

    

WITH source_data AS (

    SELECT

    airport_code,
    airport_name,
    city,
    coordinates_lon,
    coordinates_lat,
    timezone

    FROM "postgres"."dwh_detailed"."airports"
),

derived_columns AS (

    SELECT

    airport_code,
    airport_name,
    city,
    coordinates_lon,
    coordinates_lat,
    timezone,
    'POSTGRES_MASTER'::TEXT AS RECORD_SOURCE

    FROM source_data
),

hashed_columns AS (

    SELECT

    airport_code,
    airport_name,
    city,
    coordinates_lon,
    coordinates_lat,
    timezone,
    RECORD_SOURCE,

    DECODE(MD5(NULLIF(UPPER(TRIM(CAST(airport_code AS VARCHAR))), '')), 'hex') AS HUB_AIRPORTS_KEY,

    DECODE(MD5(CONCAT(
        COALESCE(NULLIF(UPPER(TRIM(CAST(airport_name AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(city AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(coordinates_lat AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(coordinates_lon AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(timezone AS VARCHAR))), ''), '^^')
    )), 'hex') AS HASHDIFF

    FROM derived_columns
),

columns_to_select AS (

    SELECT

    airport_code,
    airport_name,
    city,
    coordinates_lon,
    coordinates_lat,
    timezone,
    RECORD_SOURCE,
    HUB_AIRPORTS_KEY,
    HASHDIFF

    FROM hashed_columns
)

SELECT * FROM columns_to_select
)

