








WITH staging AS (
-- Generated by AutomateDV (formerly known as dbtvault)

    

WITH source_data AS (

    SELECT

    ticket_no,
    flight_id,
    fare_conditions,
    amount

    FROM "postgres"."dwh_detailed"."ticket_flights"
),

derived_columns AS (

    SELECT

    ticket_no,
    flight_id,
    fare_conditions,
    amount,
    'POSTGRES_MASTER'::TEXT AS RECORD_SOURCE

    FROM source_data
),

hashed_columns AS (

    SELECT

    ticket_no,
    flight_id,
    fare_conditions,
    amount,
    RECORD_SOURCE,

    DECODE(MD5(NULLIF(UPPER(TRIM(CAST(ticket_no AS VARCHAR))), '')), 'hex') AS HUB_TICKETS_KEY,

    DECODE(MD5(NULLIF(UPPER(TRIM(CAST(flight_id AS VARCHAR))), '')), 'hex') AS HUB_FLIGHTS_KEY,

    DECODE(MD5(CONCAT(
        COALESCE(NULLIF(UPPER(TRIM(CAST(amount AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(fare_conditions AS VARCHAR))), ''), '^^')
    )), 'hex') AS HASHDIFF,

    DECODE(MD5(NULLIF(CONCAT(
        COALESCE(NULLIF(UPPER(TRIM(CAST(ticket_no AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(flight_id AS VARCHAR))), ''), '^^')
    ), '^^||^^')), 'hex') AS LINK_TICKET_FLIGHTS_KEY

    FROM derived_columns
),

columns_to_select AS (

    SELECT

    ticket_no,
    flight_id,
    fare_conditions,
    amount,
    RECORD_SOURCE,
    HUB_TICKETS_KEY,
    HUB_FLIGHTS_KEY,
    HASHDIFF,
    LINK_TICKET_FLIGHTS_KEY

    FROM hashed_columns
)

SELECT * FROM columns_to_select
)

