








WITH staging AS (
-- Generated by AutomateDV (formerly known as dbtvault)

    

WITH source_data AS (

    SELECT

    ticket_no,
    book_ref,
    passenger_id,
    passenger_name,
    contact_data

    FROM "postgres"."dwh_detailed"."tickets"
),

derived_columns AS (

    SELECT

    ticket_no,
    book_ref,
    passenger_id,
    passenger_name,
    contact_data,
    'POSTGRES_MASTER'::TEXT AS RECORD_SOURCE

    FROM source_data
),

hashed_columns AS (

    SELECT

    ticket_no,
    book_ref,
    passenger_id,
    passenger_name,
    contact_data,
    RECORD_SOURCE,

    DECODE(MD5(NULLIF(UPPER(TRIM(CAST(ticket_no AS VARCHAR))), '')), 'hex') AS HUB_TICKETS_KEY,

    DECODE(MD5(NULLIF(CONCAT(
        COALESCE(NULLIF(UPPER(TRIM(CAST(ticket_no AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(book_ref AS VARCHAR))), ''), '^^')
    ), '^^||^^')), 'hex') AS LINK_TICKETS_BOOKINGS_KEY,

    DECODE(MD5(CONCAT(
        COALESCE(NULLIF(UPPER(TRIM(CAST(contact_data AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(passenger_id AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(passenger_name AS VARCHAR))), ''), '^^')
    )), 'hex') AS HASHDIFF

    FROM derived_columns
),

columns_to_select AS (

    SELECT

    ticket_no,
    book_ref,
    passenger_id,
    passenger_name,
    contact_data,
    RECORD_SOURCE,
    HUB_TICKETS_KEY,
    LINK_TICKETS_BOOKINGS_KEY,
    HASHDIFF

    FROM hashed_columns
)

SELECT * FROM columns_to_select
)

